function initMap(){return new google.maps.Map(document.getElementById("map"),{center:{lat:35.7881812,lng:-78.7951261},zoom:12,style:myMapStyles})}function makeMarkerIcon(e){return new google.maps.MarkerImage("http://chart.googleapis.com/chart?chst=d_map_spin&chld=1.15|0|"+e+"|40|_|%E2%80%A2",new google.maps.Size(21,34),new google.maps.Point(0,0),new google.maps.Point(10,34),new google.maps.Size(21,34))}function setMarkerAnimations(e){const n=makeMarkerIcon("0091ef"),t=makeMarkerIcon("FFFF24");e.addListener("click",toggleBounce),e.addListener("mouseover",function(){this.setIcon(t)}),e.addListener("mouseout",function(){this.setIcon(n)})}function updateMarkers(e,n,t){_.difference(e,n).forEach(e=>e.mapMarker.setMap(null)),_.intersection(e,n).forEach(e=>e.mapMarker.setMap(t))}function toggleBounce(){null!==this.getAnimation()?this.setAnimation(null):animateMarker.call(this)}function animateMarker(){this.setAnimation(google.maps.Animation.BOUNCE);var e=this;setTimeout(function(){e.setAnimation(null)},2100)}